import React, {useRef, useState} from 'react';
import "./contact.css";
import Phone from "../../img/icons/phone.png";
import  Email from "../../img/icons/email.png";
import Adresse from "../../img/icons/place.png";
import Home from "../../img/icons/home.png";
import emailjs from 'emailjs-com';

const Contact = () => {

    const formRef = useRef();
    const [done , setDone] = useState(false)

    const handleSubmit = (e) => {
        e.preventDefault();

        emailjs.sendForm('service_u95xrjw', 'template_ry8jn8j', formRef.current, 'KQdtVp1qxFnW7XLu4')
            .then((result) => {
                console.log(result.text);
            }, (error) => {
                console.log(error.text);
            });

    };
    return (
        <div className="c">
               <div className="c-bg"></div>
                    <div className="c-wrapper">
                        <div className="c-left">
                            <h1 className="c-title">
                                Contact me
                            </h1>
                            <div className="c-info">
                                <div className="c-info-item">
                                    <img src={Home} alt="" className="c-icon"/>mr_oreox@mail.com
                                </div>
                                <div className="c-info-item">
                                    <img src={Phone} alt="" className="c-icon"/>+12345678
                                </div>
                                <div className="c-info-item">
                                    <img src={Email} alt="" className="c-icon"/>mr_oreox@mail.com
                                </div>
                                <div className="c-info-item">
                                    <img src={Adresse} alt="" className="c-icon"/>mr_oreox@mail.com
                                </div>
                            </div>
                        </div>
                        <div className="c-rigth">
                            <p className="c-desc">
                                <b> What about your story</b> molestias nulla quibusdam sed sint vero voluptatem!
                            </p>
                            <form ref={formRef} onSubmit={handleSubmit}>
                                <input type="text" placeholder="Name" name="user_name"/>
                                <input type="text" placeholder="Subject" name="user_subject"/>
                                <input type="text" placeholder="Email" name="user_email"/>
                                <textarea rows="10" name="message" placeholder="Message"></textarea>
                                <button>Submit</button>
                                {done && "Thank You form emailing me"}
                            </form>

                        </div>
                    </div>
        </div>
      );
   };

export default Contact;
