import React, {useContext, useRef, useState} from 'react';
import "./contact.css";
import Phone from "../../img/icons/phone.png";
import  Email from "../../img/icons/email.png";
import Adresse from "../../img/icons/place.png";
import Home from "../../img/icons/home.png";
import emailjs from 'emailjs-com';
import {ThemeContext} from "../context";

const Contact = () => {

    const formRef = useRef();
    const theme = useContext(ThemeContext);
    const darkMode = theme.state.darkMode;
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [subject, setSubject] = useState("");
    const [message, setMessage] = useState("");
    const onChangeHandler = (fieldName, value)=>{
        if(fieldName === "name"){
            setName(value);
        }
        else if(fieldName==="email"){
            setEmail(value);
        }
        else if (fieldName === "subject")
        {
            setSubject(value);
        }
        else if (fieldName === "message")
        {
            setMessage(value);
        }

    }
    const onSubmitHandler = (e)=> {
        e.preventDefault();
        if (name.trim() === "" && email.trim() == "" && message.trim() == "" && subject.trim() == "")
        {
            alert("required all fields");

        }
        else if (name.trim() == "")
        {
            alert("name required");

        }
        else if (email.trim() == "")
        {
            alert("email required");

        }
        else if (message.trim() == "")
        {
            alert("message required");
        }
        else
        {
            alert("sent");
            setName("");
            setMessage("");
            setSubject("");
            setEmail("");
        }

        emailjs.sendForm('service_u95xrjw', 'template_ry8jn8j', formRef.current, 'KQdtVp1qxFnW7XLu4')
            .then((result) => {
                console.log(result.text);
            }, (error) => {
                console.log(error.text);
            });
        }
    return (
        <div className="c">
               <div className="c-bg"></div>
                    <div className="c-wrapper">
                        <div className="c-left">
                            <h1 className="c-title">
                                Contact me
                            </h1>
                            <div className="c-info">
                                <div className="c-info-item">
                                    <img src={Phone} alt="" className="c-icon"/>+12345678
                                </div>
                                <div className="c-info-item">
                                    <img src={Email} alt="" className="c-icon"/>mr_oreox@mail.com
                                </div>
                                <div className="c-info-item">
                                    <img src={Adresse} alt="" className="c-icon"/>Mustes.strasse.deutchland
                                </div>
                                <p>
                                    You can als
                                </p>
                            </div>
                        </div>
                        <div className="c-rigth">
                            <p className="c-desc">
                                <b> What about your story</b> molestias nulla quibusdam sed sint vero voluptatem!
                            </p>
                            <form  ref={formRef} onSubmit={(e)=>{onSubmitHandler(e)}}>

                                <input required style={{backgroundColor: darkMode && "#333"}} type="text" value={name} placeholder="Name "
                                       onChange={(e)=>{ onChangeHandler("name",e.target.value)}} />

                                <input required style={{backgroundColor: darkMode && "#333"}} type="text" value={subject} placeholder="Subject "
                                       onChange={(e)=>{ onChangeHandler("subject",e.target.value)}} />

                                <input required  style={{backgroundColor: darkMode && "#333"}} type="email"  value={email} placeholder="Email "
                                       onChange={(e)=>{ onChangeHandler("email",e.target.value)}} />

                                <textarea required  type="text"  value={message} placeholder="Message " rows="10"
                                       onChange={(e)=>{ onChangeHandler("message",e.target.value)}} />

                                <input required className="c-form-bt" style={{backgroundColor: darkMode && "#333"}} type="submit" value="Submit" />
                            </form>
                        </div>
                    </div>
        </div>
      );
   };

export default Contact;
